instructions = "



"

sequences = instructions.split("\n")
# [x,y] positions
positions = {
  [0,0] => '1',
  [1,0] => '2',
  [0,1] => '4',
  [1,1] => '5',
  [2,0] => '3',
  [2,1] => '6',
  [0,2] => '7',
  [1,2] => '8',
  [2,2] => '9'
}

x = 1
y = 1
final_sequence = ''

sequences.each do |sequence|
  sequence.split('').each do |command|
    # Iterate through command and modify x/y position accordingly
    case command
      when "U"
        y -= 1 unless y == 0
      when "D"
        y += 1 unless y == 2
      when "L"
        x -= 1 unless x == 0
      when "R"
        x += 1 unless x == 2
      else
    end
  end
  final_sequence += positions[[x,y]]
end

puts final_sequence
